import React from "react";

function TermPage({ setStep, formData, setFormData }) {
  const terms = [
    { years: 10, rate: "3.5%" },
    { years: 15, rate: "4.0%" },
    { years: 20, rate: "4.2%" },
    { years: 25, rate: "4.5%" },
  ];

  return (
    <div className="page">
      <h2>Mortgage Calculator</h2>
      <label>
        Property Value (£): {formData.propertyValue}
      </label>
      <br />
      <label>
        Deposit (£): {formData.deposit}
      </label>
      <br />
      <label>
        Mortgage Term (years)
        <select
          value={formData.term}
          onChange={(e) => {
            const [years, rate] = e.target.value.split(",");
            setFormData({ ...formData, term: years, interestRate: rate });
          }}
        >
          <option value="">Please select</option>
          {terms.map((t) => (
            <option key={t.years} value={[t.years, t.rate]}>
              {t.years} years (Interest Rate: {t.rate})
            </option>
          ))}
        </select>
      </label>
      <br />
      <button onClick={() => setStep(3)}>See Results</button>
      <button onClick={() => setStep(1)}>Back</button>
    </div>
  );
}

export default TermPage;
