import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
import InputPage from "./pages/InputPage";
import TermPage from "./pages/TermPage";
import ResultPage from "./pages/ResultPage";

function App() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    propertyValue: "",
    deposit: "",
    term: "",
    interestRate: "",
  });

  return (
    <div className="app">
      {step === 1 && <InputPage setStep={setStep} formData={formData} setFormData={setFormData} />}
      {step === 2 && <TermPage setStep={setStep} formData={formData} setFormData={setFormData} />}
      {step === 3 && <ResultPage setStep={setStep} formData={formData} />}
    </div>
  );
}

export default App;
